#!/bin/bash\n\n# Build and deploy script for GitHub Pages\nset -e\n\necho \"🚀 Building and deploying 8 Spinner to GitHub Pages...\"\n\n# Install dependencies\necho \"📦 Installing dependencies...\"\nnpm ci\n\n# Build project\necho \"🔨 Building project...\"\nnpm run build\n\n# Deploy to gh-pages branch\necho \"🌐 Deploying to GitHub Pages...\"\nnpm run deploy\n\necho \"✅ Deployment complete! Your spinner will be available at:\"\necho \"📍 https://farkhanmaul.github.io/spinner/\""