<template>\n  <div id=\"app\" class=\"min-h-screen p-4\">\n    <div class=\"max-w-4xl mx-auto\">\n      <!-- Header -->\n      <AppHeader />\n\n      <div class=\"grid lg:grid-cols-3 gap-8\">\n        <!-- Controls Panel -->\n        <div class=\"lg:col-span-1 space-y-6\">\n          <ParticipantInput\n            v-model=\"newParticipant\"\n            @add=\"handleAddParticipant\"\n          />\n\n          <SettingsPanel\n            :sound-enabled=\"soundEnabled\"\n            :confetti-enabled=\"confettiEnabled\"\n            :spin-duration=\"spinDuration\"\n            @toggle-sound=\"toggleSound\"\n            @toggle-confetti=\"toggleConfetti\"\n            @update-duration=\"updateSpinDuration\"\n          />\n\n          <ParticipantsList\n            :participants=\"participants\"\n            @remove=\"removeParticipant\"\n            @clear=\"clearAll\"\n          />\n        </div>\n\n        <!-- Spinner -->\n        <div class=\"lg:col-span-2\">\n          <div class=\"glass rounded-3xl p-8 shadow-2xl\">\n            <SpinnerWheel\n              :participants=\"participants\"\n              :segments=\"wheelSegments\"\n              :is-spinning=\"isSpinning\"\n              :winner=\"winner\"\n              :spinner-ref=\"spinnerRef\"\n              @spin=\"handleSpin\"\n              @segment-click=\"handleSegmentClick\"\n            />\n          </div>\n        </div>\n      </div>\n    </div>\n\n    <!-- Confetti Canvas -->\n    <canvas ref=\"confettiCanvas\" id=\"confetti-canvas\"></canvas>\n  </div>\n</template>\n\n<script setup>\nimport { ref, onMounted } from 'vue'\nimport { useParticipants } from './composables/useParticipants'\nimport { useSettings } from './composables/useSettings'\nimport { useSpinner } from './composables/useSpinner'\nimport { useSound } from './composables/useSound'\nimport { useConfetti } from './composables/useConfetti'\n\n// Components\nimport AppHeader from './components/AppHeader.vue'\nimport ParticipantInput from './components/ParticipantInput.vue'\nimport SettingsPanel from './components/SettingsPanel.vue'\nimport ParticipantsList from './components/ParticipantsList.vue'\nimport SpinnerWheel from './components/SpinnerWheel.vue'\n\n// Composables\nconst { participants, newParticipant, addParticipant, removeParticipant, clearAll, loadParticipants } = useParticipants()\nconst { soundEnabled, confettiEnabled, spinDuration, toggleSound, toggleConfetti, updateSpinDuration, loadSettings } = useSettings()\nconst { isSpinning, winner, spinnerRef, wheelSegments, spin } = useSpinner()\nconst { playTickSound } = useSound()\nconst { initializeConfetti } = useConfetti()\n\n// Canvas ref\nconst confettiCanvas = ref(null)\n\n// Event handlers\nconst handleAddParticipant = (name) => {\n  addParticipant(name)\n}\n\nconst handleSpin = () => {\n  const settings = {\n    soundEnabled: soundEnabled.value,\n    confettiEnabled: confettiEnabled.value,\n    spinDuration: spinDuration.value\n  }\n  spin(settings)\n}\n\nconst handleSegmentClick = (index) => {\n  if (isSpinning.value) return\n  \n  if (soundEnabled.value) {\n    playTickSound()\n  }\n  \n  // Could add segment highlight logic here\n}\n\n// Lifecycle\nonMounted(() => {\n  loadParticipants()\n  loadSettings()\n  initializeConfetti(confettiCanvas.value)\n})\n</script>"