<template>\n  <div class=\"glass rounded-2xl p-6 shadow-xl\">\n    <h3 class=\"text-xl font-semibold mb-4 text-gray-800\">Add Participant</h3>\n    <div class=\"flex gap-2\">\n      <input\n        v-model=\"localNewParticipant\"\n        @keypress.enter=\"handleAdd\"\n        type=\"text\"\n        :placeholder=\"placeholder\"\n        :maxlength=\"maxLength\"\n        class=\"flex-1 px-4 py-3 border border-gray-200 rounded-xl focus:ring-2 focus:ring-blue-500 focus:border-transparent transition-all\"\n      />\n      <button\n        @click=\"handleAdd\"\n        :disabled=\"!localNewParticipant.trim()\"\n        class=\"px-6 py-3 bg-gradient-to-r from-blue-500 to-purple-600 text-white rounded-xl hover:from-blue-600 hover:to-purple-700 transition-all disabled:opacity-50 disabled:cursor-not-allowed font-medium\"\n      >\n        {{ buttonText }}\n      </button>\n    </div>\n  </div>\n</template>\n\n<script setup>\nimport { ref } from 'vue'\nimport { BUTTON_TEXT, PARTICIPANT_CONFIG } from '../utils/constants'\n\nconst props = defineProps({\n  modelValue: {\n    type: String,\n    default: ''\n  },\n  placeholder: {\n    type: String,\n    default: 'Enter name...'\n  },\n  buttonText: {\n    type: String,\n    default: BUTTON_TEXT.ADD\n  },\n  maxLength: {\n    type: Number,\n    default: PARTICIPANT_CONFIG.MAX_NAME_LENGTH\n  }\n})\n\nconst emit = defineEmits(['update:modelValue', 'add'])\n\nconst localNewParticipant = ref(props.modelValue)\n\nconst handleAdd = () => {\n  if (!localNewParticipant.value.trim()) return\n  \n  emit('add', localNewParticipant.value)\n  localNewParticipant.value = ''\n  emit('update:modelValue', '')\n}\n</script>"