<template>\n  <div class=\"flex items-center justify-between p-3 bg-white/50 rounded-lg border border-white/30\">\n    <span class=\"font-medium text-gray-800\">{{ participant.name }}</span>\n    <button\n      @click=\"$emit('remove')\"\n      class=\"w-6 h-6 text-red-500 hover:text-red-700 hover:bg-red-50 rounded-full flex items-center justify-center transition-colors\"\n      :aria-label=\"`Remove ${participant.name}`\"\n    >\n      {{ removeIcon }}\n    </button>\n  </div>\n</template>\n\n<script setup>\nimport { BUTTON_TEXT } from '../utils/constants'\n\ndefineProps({\n  participant: {\n    type: Object,\n    required: true\n  },\n  removeIcon: {\n    type: String,\n    default: BUTTON_TEXT.REMOVE\n  }\n})\n\ndefineEmits(['remove'])\n</script>"