<template>\n  <div class=\"glass rounded-2xl p-6 shadow-xl\">\n    <h3 class=\"text-xl font-semibold mb-4 text-gray-800\">Settings</h3>\n    \n    <div class=\"space-y-4\">\n      <ToggleSwitch\n        :enabled=\"soundEnabled\"\n        label=\"Sound Effects\"\n        @toggle=\"$emit('toggle-sound')\"\n      />\n      \n      <RangeSlider\n        :value=\"spinDuration\"\n        :min=\"minDuration\"\n        :max=\"maxDuration\"\n        :step=\"durationStep\"\n        label=\"Spin Duration\"\n        unit=\"s\"\n        @update=\"$emit('update-duration', $event)\"\n      />\n      \n      <ToggleSwitch\n        :enabled=\"confettiEnabled\"\n        label=\"Confetti\"\n        @toggle=\"$emit('toggle-confetti')\"\n      />\n    </div>\n  </div>\n</template>\n\n<script setup>\nimport ToggleSwitch from './ToggleSwitch.vue'\nimport RangeSlider from './RangeSlider.vue'\nimport { SETTINGS_CONFIG } from '../utils/constants'\n\ndefineProps({\n  soundEnabled: Boolean,\n  confettiEnabled: Boolean,\n  spinDuration: Number,\n  minDuration: {\n    type: Number,\n    default: SETTINGS_CONFIG.SPIN_DURATION.MIN\n  },\n  maxDuration: {\n    type: Number,\n    default: SETTINGS_CONFIG.SPIN_DURATION.MAX\n  },\n  durationStep: {\n    type: Number,\n    default: SETTINGS_CONFIG.SPIN_DURATION.STEP\n  }\n})\n\ndefineEmits(['toggle-sound', 'toggle-confetti', 'update-duration'])\n</script>"