<template>\n  <button\n    @click=\"$emit('spin')\"\n    :disabled=\"disabled\"\n    :class=\"buttonClasses\"\n  >\n    {{ buttonText }}\n  </button>\n</template>\n\n<script setup>\nimport { computed } from 'vue'\nimport { BUTTON_TEXT, PARTICIPANT_CONFIG } from '../utils/constants'\n\nconst props = defineProps({\n  disabled: Boolean,\n  isSpinning: Boolean,\n  participantsCount: {\n    type: Number,\n    required: true\n  }\n})\n\ndefineEmits(['spin'])\n\nconst buttonClasses = computed(() => [\n  'px-12 py-4 rounded-2xl font-bold text-xl transition-all duration-300 transform',\n  props.disabled\n    ? 'bg-gray-400 text-gray-600 cursor-not-allowed'\n    : 'bg-gradient-to-r from-red-500 to-pink-600 text-white hover:from-red-600 hover:to-pink-700 hover:scale-105 hover:shadow-2xl active:scale-95'\n])\n\nconst buttonText = computed(() => {\n  if (props.participantsCount < PARTICIPANT_CONFIG.MIN_PARTICIPANTS) {\n    return BUTTON_TEXT.SPIN.NOT_ENOUGH\n  }\n  if (props.isSpinning) {\n    return BUTTON_TEXT.SPIN.SPINNING\n  }\n  return BUTTON_TEXT.SPIN.DEFAULT\n})\n</script>"