<template>\n  <g>\n    <path\n      :d=\"segment.path\"\n      :fill=\"segment.color\"\n      stroke=\"#ffffff\"\n      stroke-width=\"2\"\n      class=\"spinner-segment cursor-pointer transition-all hover:brightness-110\"\n      @click=\"$emit('click')\"\n    />\n    <text\n      :x=\"segment.textX\"\n      :y=\"segment.textY\"\n      :transform=\"`rotate(${segment.textRotation}, ${segment.textX}, ${segment.textY})`\"\n      fill=\"white\"\n      text-anchor=\"middle\"\n      dominant-baseline=\"middle\"\n      class=\"text-sm font-bold pointer-events-none\"\n      style=\"text-shadow: 1px 1px 2px rgba(0,0,0,0.7)\"\n    >\n      {{ displayName }}\n    </text>\n  </g>\n</template>\n\n<script setup>\nimport { computed } from 'vue'\nimport { truncateText } from '../utils/validation'\n\nconst props = defineProps({\n  segment: {\n    type: Object,\n    required: true\n  },\n  participant: {\n    type: Object,\n    required: true\n  }\n})\n\ndefineEmits(['click'])\n\nconst displayName = computed(() => \n  truncateText(props.participant.name, 8)\n)\n</script>"