<template>\n  <div class=\"flex flex-col items-center\">\n    <!-- Spinner Container -->\n    <div class=\"relative mb-8\">\n      <div class=\"spinner-container relative\">\n        <svg\n          ref=\"spinnerRef\"\n          :class=\"wheelClasses\"\n          width=\"350\"\n          height=\"350\"\n          viewBox=\"0 0 350 350\"\n        >\n          <!-- Wheel segments -->\n          <g v-if=\"segments.length > 0\">\n            <SpinnerSegment\n              v-for=\"(segment, index) in segments\"\n              :key=\"index\"\n              :segment=\"segment\"\n              :participant=\"participants[index]\"\n              @click=\"$emit('segment-click', index)\"\n            />\n          </g>\n          \n          <!-- Empty state -->\n          <EmptyWheel v-else />\n          \n          <!-- Center circle -->\n          <circle cx=\"175\" cy=\"175\" r=\"15\" fill=\"#1f2937\" />\n        </svg>\n        \n        <!-- Pointer -->\n        <SpinnerPointer />\n      </div>\n    </div>\n\n    <!-- Spin Button -->\n    <SpinButton\n      :disabled=\"isDisabled\"\n      :is-spinning=\"isSpinning\"\n      :participants-count=\"participants.length\"\n      @spin=\"$emit('spin')\"\n    />\n\n    <!-- Winner Display -->\n    <WinnerDisplay :winner=\"winner\" />\n  </div>\n</template>\n\n<script setup>\nimport { computed } from 'vue'\nimport SpinnerSegment from './SpinnerSegment.vue'\nimport SpinnerPointer from './SpinnerPointer.vue'\nimport SpinButton from './SpinButton.vue'\nimport WinnerDisplay from './WinnerDisplay.vue'\nimport EmptyWheel from './EmptyWheel.vue'\nimport { PARTICIPANT_CONFIG } from '../utils/constants'\n\nconst props = defineProps({\n  participants: {\n    type: Array,\n    required: true\n  },\n  segments: {\n    type: Array,\n    required: true\n  },\n  isSpinning: Boolean,\n  winner: Object,\n  spinnerRef: Object\n})\n\ndefineEmits(['spin', 'segment-click'])\n\nconst isDisabled = computed(() => \n  props.participants.length < PARTICIPANT_CONFIG.MIN_PARTICIPANTS || props.isSpinning\n)\n\nconst wheelClasses = computed(() => [\n  'spinner-wheel transition-transform duration-1000 ease-out',\n  { 'animate-pulse-glow': props.isSpinning }\n])\n</script>\n\n<style scoped>\n.spinner-wheel {\n  filter: drop-shadow(0 10px 25px rgba(0, 0, 0, 0.15));\n}\n</style>"